import React, { useState, useEffect } from 'react';
import MoviesItem from './moviesItem';
function OMDbApi(props) {
    let [movies_arr, setMoviesArr] = useState([]);

    let url = "http://www.omdbapi.com/?s="+props.userInput+"&apikey=5a292f28";
    useEffect(() => {
        fetch(url)
            .then(resp => resp.json())
            .then(data => {
                console.log(data.Search);
                setMoviesArr(data.Search)
            })
            .catch(err => {
                alert("testing: " + err);
                console.log(err);

            })
    }, [props.userInput])

    return (

        <div className="container">
            <div className="row mt-3">
                {(movies_arr && props.userInput !=="") ? movies_arr.map(item => {
                    return (
                        <MoviesItem key={item.imdbID} item={item} />
                    )
                }) : "Not found"
                }

            </div>
        </div>
    )
}

export default OMDbApi
