import React, { useState, useEffect } from 'react';
function DarkBox(props) {
    // alert(props.imdbID)
    let [imd, setImd] = useState(null);
    let [plot, setPlot] = useState("more");
    // let [read, setRead] = useState("Read More");


    useEffect(() => {

        let url = "http://www.omdbapi.com/?i=" + props.imdbID + "&apikey=5a292f28"
        fetch(url)
            .then(resp => resp.json())
            .then(data => {
                console.log(data);
                setImd(data)
                setPlot(data.Plot.substring(0, 50))

            })
    }, [props.imdbID])

    // useEffect(()=>{
    //     if(read ==="Read More"){
    //         setRead("less");
    //         // setRead("Read More");
    //         // setPlot(imd.Plot);
    //     }
    //     else{
    //         setRead("less");
    //         // setPlot(imd.Plot.substring(0, 50));
    //     }
    // },[])



    return (
        <div id="id_dark" className="dark container-fluid center">
            <div className="dark_inside text-center">
                {(imd) ? <div><img src={imd.Poster} />
                    <h2>{imd.Title}</h2>
                    <h2>{imd.Year}</h2>
                    <p>{(plot == "more") ? <p className="text-primary">less</p>
                     : <p className="text-primary">more</p>}</p>
                </div> : ""}
                <button className="btn btn-danger sm-3" onClick={() => {
                    props.setOpenDark(false);
                }}>Close</button>
            </div>
        </div>
    )
}

export default DarkBox
