import React, { useRef, useState } from 'react';
function Search(props) {
    let searchInput = useRef()
    // let testing = useRef()
    let [test, setTest] = useState("")


    const onInput = () => {
        // alert(searchInput.current.value);
        setTest (searchInput.current.value);


    }


    return (
        <div className="container d-flex justify-content-center align-items-center searchBox">
            <div className="col-lg-6">
            <input ref={searchInput} onInput={onInput} type="text" placeholder="search ..." className="form-control w-75" />
            <div >{test}</div>
            </div>
        </div>

        //         <div className="input-group col-md-4  container d-flex justify-content-center align-items-center searchBox">
        //        <input className="form-control py-2 border-right-0 border" type="text" value="search"/>
        //             <span className="input-group-append">
        //               <button className="btn btn-outline-secondary border-left-0 border" type="button">
        //                     <i className="fa fa-search"></i>
        //               </button>
        //             </span>
        // </div>
    )
}

export default Search
