import React, { useRef, useState } from 'react';
import SearchIcon from '@material-ui/icons/Search';
import { TextField, InputAdornment, IconButton } from '@material-ui/core';
function Search(props) {
    let searchInput = useRef()
    let [inputLocation, setInputLocation] = useState("css_searchBoxCenter")

    const onInput = () => {
        if (searchInput.current.value === "") {
            setInputLocation("css_searchBoxCenter")
        }
        else {
            setInputLocation("css_searchBoxTop")
        }
        props.setUserInput(searchInput.current.value);
    }

    return (
        <div className="container" id="id_search">
            <div className={inputLocation}>
            <input ref={searchInput} onInput={onInput} type="text" placeholder="search ..." className="form-control w-75 searchBox_input" id="id_input" />
            <SearchIcon/>

            <TextField id="outlined-basic" placeholder="search ..." variant="outlined" className="form-control w-75 searchBox_input" InputProps={{
                        endAdornment: (
                            <InputAdornment >
                                <IconButton>
                                    <SearchIcon />
                                </IconButton>
                            </InputAdornment>
                        )
                    }}
 />

            </div>
        </div>
    )
}

export default Search
