import React, { useRef, useState } from 'react';
import OMDbApi from './omdbApi';
function Search(props) {
    let searchInput = useRef()
    let [inputLocation, setInputLocation] = useState("css_searchBoxCenter")


    const onInput = () => {
        if (searchInput.current.value === "") {
            setInputLocation("css_searchBoxCenter")
        }
        else {
            setInputLocation("css_searchBoxTop")
        }
        props.setUserInput(searchInput.current.value);
    }

    return (
        <div className="container" id="id_search">
            <div className={inputLocation}>
            <input ref={searchInput} onInput={onInput} type="text" placeholder="search ..." className="form-control py-2 border-right-0 border " id="id_input" />
            <span className="input-group-append">
                <button className="btn btn-outline-secondary border-left-0 border" type="button">
                    <i className="fa fa-search"></i>
                </button>
            </span>
            </div>
        </div>


        // <div className="input-group col-md-4  container d-flex justify-content-center align-items-center searchBox">
        //     <input className="form-control py-2 border-right-0 border" type="text" value="search" />
        //     <span className="input-group-append">
        //         <button className="btn btn-outline-secondary border-left-0 border" type="button">
        //             <i className="fa fa-search"></i>
        //         </button>
        //     </span>
        // </div>
    )
}

export default Search
