import React, { Component } from "react";

class Exchange extends Component {
    cal = 0;
    h2_text = "It will cost you XXX NIS";

    exchange = () => {
        // alert(this.refs.id_select.value);
        this.cal = (this.refs.id_select.value) * (this.refs.id_input.value);
        this.refs.resultField.innerHTML = "It will cost you "+this.cal +" NIS";
        this.forceUpdate();
        


    }
    exchangeOninput = () => {
        this.refs.resultField.innerHTML = "It will cost you "+this.cal+" NIS";
        // alert(this.refs.id_input.value);

    }

    render() {
        return (
            <div className="container border border-info mt-4">
                <h1>Exchange</h1>
                <div className="row">
                    <div className="col-lg 6">
                        <h2>Enter the currency you want to exchange:</h2>
                        <select ref="id_select" onChange={this.exchange} className="w-25">
                            <option value=" ">choose coin</option>
                            <option value="3.4508">USD</option>
                            <option value="3.8571">EUR</option>
                            <option value="0.1113">THB</option>
                        </select>
                        <h2>Enter the amount you want:</h2>
                        <input type="number" min="1" ref="id_input" onInput={this.exchangeOninput} className="w-25" defaultValue="1"/>
                        <h2 ref="resultField">{this.h2_text}</h2>

                    </div>

                </div>

            </div>

        )
    }
}

export default Exchange;
